    val gameListData: ArrayList<GamesDescData>
        get() { // GET DATA FROM GAMEDESCDATA
            val gameList = arrayListOf<GamesDescData>()
            for (data in gamesName.indices) {
                val game = GamesDescData()
                game.name = gamesName[data]
                game.desc = gameDesc[data]
                game.release_date = gameReleaseDate[data]
                game.rating = gameRating[data]
                game.image = gameImage[data]
                game.image_large = gameImageLarge[data]
                gameList.add(game)
